sp_widget.client_script:
function($rootScope,$scope) {
  /* widget controller */
  var c = this;
	$rootScope.$on('selectednoteevt',function(data,event){
		console.log("Passed in event data sys_id "+$rootScope.notesys_id);
		console.log("Passed in event data "+ data + " and event is " +event );
		c.server.get({
			action: "getNote",
			noteId: $rootScope.notesys_id
		}).then(function(r){
			c.data.sys_id = r.data.note.sys_id;
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
		});
	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.note ={};
	gs.addInfoMessage("Notes Body widget "+JSON.stringify(input));
	if(input && input.noteId)
	{
		if(input.action == 'getNote')
		{
			var gr = new GlideRecord('x_snc_createnotes_note');
			gr.addQuery('sys_id',input.noteId);
			//gr.orderByDesc('sys_created_on'); 
			gr.query();
			if(gr.next()){
				$sp.getRecordValues(data.note, gr, 'title,note,sys_id');

			}
		}
	}
	else{
		data.note.sys_id ='123';
		data.note.title ="Went to ELSE of Notes BOdy Server Side Script Widget"
	}


})();
