sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;
	var csys_id;
	c.selectItem = function(idx)
	{
		alert("Index of Selected Item is "+idx);
		alert("Array crated from SS "+JSON.stringify(c.data.sparr[idx].sys_id));
		csys_id=c.data.sparr[idx].sys_id;
		console.log("selected record sys_id is "+csys_id);
		$rootScope.notesys_id = csys_id;
		$rootScope.$emit('selectednoteevt',csys_id);
	}
}

