sp_widget.client_script:
function(spUtil,$scope,$rootScope) {
	/* widget controller */
	var c = this;
	//	alert("Array server script :"+JSON.stringify(c.data.notes));
	//	alert("Test Server script :"+c.data.testvalue);
var outeridx =0;
	c.selectItem = function(idx)
	{
		//	spUtil.addInfoMessage("Index of selected note "+idx);
		//alert("Index of selected note "+idx);
		outeridx =idx;
		var id = c.data.notes[idx].sys_id;
		//	alert("Sys_id of selected Note is "+id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
		//	console.log('Note ID: ' + id);
	}

	$rootScope.$on('refreshNote',function(event,data){
		//alert("Refresh Note ON");
		console.log("Data object passed from Note Body widget "+JSON.stringify(data));
		c.data.notes[outeridx].nnotes = data.note;
		c.data.notes[outeridx].title = data.title;

		/*	c.server.get({
			action: "refreshNote",
			noteID : $rootScope.modsys_id,
			notetitle: $rootScope.modtitle,
			notenote: $rootScope.modnote
		}).

		then(function(r){
			alert("List refreshed");

		});*/
	});
}

