sp_widget.client_script:
function() {	
	var c = this;		
	var property="";
	alert("Server Data :"+JSON.stringify(c.data));
  c.display = function() {		
		c.data.message = (c.data.sometext) ? 'Hello ' + c.data.sometext + '!' : '';
		alert("Display Function: "+c.data.message);
		alert("Server Data along with Disp function :"+JSON.stringify(c.data));
		for(var property in c.data){
		console.log('c.data.' + property + ": " + c.data[property]);
	}
	}

	c.display();
}

